        addressDto.setAddress1((String) recordMap.get("OC_BA_ADDRESS"));
        addressDto.setZipCode((String) recordMap.get("OC_BA_ZIPCODE"));
        addressDto.setCity((String) recordMap.get("OC_BA_CITY"));
