-- Create new partitioned table execute 'create table '||schema_name||'.billing_rated_transaction ( id bigint not null default nextval('''||schema_name||'.billing_rated_transaction_seq''::regclass), version integer, amount_tax numeric(23,12), amount_with_tax numeric(23,12), amount_without_tax numeric(23,12), code character varying(255), description character varying(255), do_not_trigger_invoicing integer not null default 0, offer_code character varying(255), parameter_1 character varying(255), parameter_2 character varying(255), parameter_3 character varying(255), quantity numeric(23,12), status character varying(255), unit_amount_tax numeric(23,12), unit_amount_with_tax numeric(23,12), unit_amount_without_tax numeric(23,12), unity_description character varying(20), usage_date timestamp without time zone, wallet_operation_id bigint, billing_account__id bigint, billing_run_id bigint, invoice_id bigint, aggregate_id_f bigint, aggregate_id_r bigint, aggregate_id_t bigint, invoice_sub_category_id bigint, priceplan_id bigint, wallet_id bigint, edr_id bigint, adjusted_rated_tx bigint, order_number character varying(100), rating_unit_description character varying(20), parameter_extra text, start_date timestamp without time zone, end_date timestamp without time zone, subscription_id bigint, seller_id bigint not null, charge_instance_id bigint, tax_id bigint, tax_percent numeric(23,12), user_account_id bigint, constraint billing_rated_transaction_pkey primary key (id, usage_date), constraint fk_8g8jlmakt1wyw73oxs5yltlsd foreign key (aggregate_id_t) references '||schema_name||'.billing_invoice_agregate (id) match simple on update no action on delete no action, constraint fk_amwacie0nqfumpd1sc7y9nv0p foreign key (aggregate_id_r) references '||schema_name||'.billing_invoice_agregate (id) match simple on update no action on delete no action, constraint fk_anslvrbm465ossqx8surjyu22 foreign key (wallet_id) references '||schema_name||'.billing_wallet (id) match simple on update no action on delete no action, constraint fk_bbnhg8vn7jkttghphr65iwug5 foreign key (billing_account__id) references '||schema_name||'.billing_billing_account (id) match simple on update no action on delete no action, constraint fk_billing_rated_transaction_crm_seller foreign key (seller_id) references '||schema_name||'.crm_seller (id) match simple on update no action on delete no action, constraint fk_eucl7of9ovyut1c4r2jkf01u6 foreign key (priceplan_id) references '||schema_name||'.cat_price_plan_matrix (id) match simple on update no action on delete no action, constraint fk_gq86s178gc3orumcl8symk4nn foreign key (invoice_id) references '||schema_name||'.billing_invoice (id) match simple on update no action on delete no action, constraint fk_o2mp1mqcyi2l7q2b1p88h20dw foreign key (billing_run_id) references '||schema_name||'.billing_billing_run (id) match simple on update no action on delete no action, constraint fk_o5yu3c0bnac626w755w5f0voc foreign key (invoice_sub_category_id) references '||schema_name||'.billing_invoice_sub_cat (id) match simple on update no action on delete no action, constraint fk_rjpm9k6xsumg297fxefffyryh foreign key (aggregate_id_f) references '||schema_name||'.billing_invoice_agregate (id) match simple on update no action on delete no action, constraint fk_rt_tax foreign key (tax_id) references '||schema_name||'.billing_tax (id) match simple on update no action on delete no action, constraint fk_rt_ua foreign key (user_account_id) references '||schema_name||'.account_entity (id) match simple on update no action on delete no action ) partition by range (usage_date)';
