-- Create required partitions. Number of partitions is calculated between min and max partition date based on a frequency IF _min_date IS NOT NULL and frequency IS NOT NULL THEN
_partitions_number := _max_date - _min_date;
IF frequency = 'M' THEN _partitions_number := _partitions_number/30; END IF;
IF frequency = 'Y' THEN _partitions_number := _partitions_number/365; END IF;
IF frequency = 'W' THEN _partitions_number := _partitions_number/7; END IF;
PERFORM create_partitions(schema_name || '.billing_rated_transaction', to_char(_min_date,'YYYY_MM_DD'), frequency, _partitions_number+1); END IF;
-- Populate with old data execute 'insert into '||schema_name||'.billing_rated_transaction select * from '||schema_name||'.billing_rated_transaction_tmp;';
-- Drop temp data table execute 'drop table '||schema_name||'.billing_rated_transaction_tmp cascade;';
-- Recreate sequence execute 'CREATE SEQUENCE IF NOT EXISTS '|| schema_name || '.billing_rated_transaction_seq START '||_seq_value||';';
END; $$ LANGUAGE plpgsql;

