def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
// Set Namespaces
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

def dateFoundStr = holder.getNodeValue("//billingAccount/nextInvoiceDate")
def dateFound = new Date().parse("yyyy-MM-dd'T'hh:mm:ssXXX", dateFoundStr)
dateFoundStr = dateFound.format("yyyy-MM-dd")



Calendar c = Calendar.getInstance();   // this takes current date
    c.set(Calendar.DAY_OF_MONTH, 1);
    c.add(GregorianCalendar.MONTH, 1 );
def nextMonth=c.getTime().format("yyyy-MM-dd")
assert dateFoundStr == nextMonth

